	//ステージ情報mapchip_class情報で
	//static mapchip[][] mapchip_stage_data = new mapchip[Stage_1.length][Stage_1[0].length];


	public Stage() {
		baseX = GameController.start_base_x;
		baseY = GameController.start_base_y;

		this.frame = new JFrame("NE26-0010 田中悠生");
		this.frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		this.frame.pack();
		Insets is = this.frame.getInsets();	//ウインドウの枠の情報取得
		frame.setSize(PANEL_W + is.left + is.right, PANEL_H + is.top + is.bottom);
		this.frame.setResizable(false);
		(this.frame.getContentPane()).add(this);

		Stage.stage = this;

		try {
			//画像の読み込み
			//空
			URL url_0=this.getClass().getResource("img/field/sky.png");
			Stage_imgs[0] = this.createImage((ImageProducer) url_0.getContent());
			//青
			URL url_1=this.getClass().getResource("img/field/blue.png");
			Stage_imgs[1] = this.createImage((ImageProducer) url_1.getContent());
			//緑
			URL url_2 = this.getClass().getResource("img/field/green.png");
			Stage_imgs[2] = this.createImage((ImageProducer) url_2.getContent());
			//橙
			URL url_3 = this.getClass().getResource("img/field/orange.png");
			Stage_imgs[3] = this.createImage((ImageProducer) url_3.getContent());
			//桃
			URL url_4 = this.getClass().getResource("img/field/pink.png");
			Stage_imgs[4] = this.createImage((ImageProducer) url_4.getContent());
			//赤
			URL url_5=this.getClass().getResource("img/field/red.png");
			Stage_imgs[5] = this.createImage((ImageProducer) url_5.getContent());
			//水色
			URL url_6 = this.getClass().getResource("img/field/skyblue.png");
			Stage_imgs[6] = this.createImage((ImageProducer) url_6.getContent());
			//黄色
			URL url_7 = this.getClass().getResource("img/field/yellow.png");
			Stage_imgs[7] = this.createImage((ImageProducer) url_7.getContent());

			//実験
			/*URL url_10 = this.getClass().getResource("img/enemy_1/enemy_1.png");
			enemy = this.createImage((ImageProducer) url_10.getContent());
			System.out.println(enemy);*/

		}	catch(Exception ex) {
			System.out.println("Resource Error!!!!");
			System.out.println(ex.toString());
		}

	}

	//paintComponentメソッドのオーバーライド
	public void paintComponent(Graphics g) {
		super.paintComponent(g);
		g.translate(this.baseX, this.baseY);
		//System.out.println("baseX:"+ this.baseX+ ";  baseY:"+this.baseY);
		write_map(this.write_point_x,this.write_point_y,g);
		player_.paintActors(g, this);
		//敵描画
		for(int i =0; i<enemys.length; i++){
			if (!(enemys[i] == null)){
				//System.out.println(i);
				enemys[i].paintEnemys(g, this);
			}
		}
	}

	public void write_map(int point_x, int point_y, Graphics g) {	//pointを基準にマップを描画
		for (int i=point_y-1; i<point_y+PANEL_H_NUM+1; i++) {
			//System.out.println("point_-------------------:x="+point_x+":y="+point_y+":stage.basex="+Stage.baseX+":Stage.baseY="+Stage.baseY);
			for (int j=point_x-1; j<point_x+PANEL_W_NUM+1; j++){
				//System.out.println("j:"+j+"  ; i:"+i);
				//おそらく画像を読み込めない初期段階はエラーがでる（動くが）
				int map_num_ = Stage_1[i][j];	//マップの種類
				mapchip mc = new mapchip(Stage_imgs[map_num_],i,j);
				mc.paintActors(g, this);
			}
		}
	}

	//Playerクラスを登録
	public void set_player(player rock) {
		player_ = rock;
	}

	//Enemyクラスを登録
	public void set_enemy(Enemy[] enemys) {
		this.enemys = enemys;
	}

